<template>
  <div class="px-4">
    <v-sheet color="transparent">
      <v-row>
        <v-col cols="12" lg="6" xl="7">
          <v-card class="pa-10 elevation-5" outlined>
            <div class="d-flex align-center justify-space-between">
              <div>
                <div class="text-h6">
                  待处理缴费信息
                </div>
              </div>
            </div>
            <template>
              <v-data-table
                  :headers="headers"
                  :items="desserts"
                  :items-per-page="5"
                  class="elevation-24"
                  :loading = "load"
                  loading-text="加载中..."
              >
                <template v-slot:item.actions="{ item }">
                  <v-btn color="primary" @click="paymoney(item)"  class="elevation-5">缴 费</v-btn>
                </template>
                <template v-slot:no-data>
                  暂无待缴费信息
                </template>
              </v-data-table>
            </template>
          </v-card>
          <v-col></v-col>

          <v-card outlined class="pa-10 elevation-5">
            <div class="d-flex align-center justify-space-between">
              <div class="text-h6">
                已提交的维修记录
              </div>
              <v-spacer/>
              <v-dialog
                  v-model="dialog"
                  max-width="300px"
              >
                <template v-slot:activator="{ on, attrs }">
                  <v-btn
                      color="primary"
                      dark
                      class="mb-2  elevation-5"
                      v-bind="attrs"
                      v-on="on"
                  >
                    报 修
                  </v-btn>
                </template>
                <v-card ref="form">
                  <v-card-title>
                    <span class="headline">添加报修记录</span>
                  </v-card-title>
                  <v-card-text>
                    <v-container>
                      <v-col>
                        <v-select
                            :items="memo"
                            label="类型"
                            v-model="memo_sort"
                        ></v-select>
                      </v-col>
                      <v-row>
                          <v-textarea
                              outlined
                              auto-grow
                              clearable
                              clear-icon="mdi-close-circle"
                              v-model="savelog"
                              label="详 情*"
                              counter="50"
                              required
                              :error-messages="logErrors"
                              @input="$v.savelog.$touch()"
                              @blur="$v.savelog.$touch()"
                          ></v-textarea>
                        <v-spacer/>
                      </v-row>
                      <small>带*为必填项</small>
                    </v-container>
                  </v-card-text>

                  <v-card-actions>
                    <v-spacer/>
                    <v-btn
                        color="blue darken-1"
                        text
                        @click="close"

                    >
                      取 消
                    </v-btn>
                    <v-btn
                        color="blue darken-1"
                        text
                        @click="save"
                    >
                      报 修
                    </v-btn>
                  </v-card-actions>
                </v-card>
              </v-dialog>
            </div>

            <template>
              <v-data-table
                  :headers="fixheader"
                  :items="fix"
                  :items-per-page="5"
                  class="elevation-24"
                  :loading = "load"
                  loading-text="Loading... Please wait"
              >
                <template v-slot:item.fix_status="{item }">
                  <v-chip :color="getColor(item.fix_status)" dark  class="elevation-5" outlined >{{ item.fix_status}}</v-chip>
                </template>
                <template v-slot:no-data>
                  暂无报修记录
                </template>

                <template v-slot:item.actions="{ item }">
                  <v-btn  v-if="item.fix_status!=='未处理'" icon color="primary" class="elevation-5 ma-2" @click="timelineItem(item)">
                    <v-icon small  >{{mdiTimelineTextOutline}}</v-icon>
                  </v-btn>
                </template>

                <template v-slot:top>
                <v-dialog class="rounded-0" v-model="dialogfixlog" max-width="520px">
                  <v-card  class="rounded-0">
                    <v-card-title>
                      <span class="headline">查看维修过程记录</span>
                    </v-card-title>

                    <v-card-text>
                      <v-timeline
                          align-top
                          dense
                      >
                        <v-timeline-item
                            v-for="(item, i) in tl"
                            :key="i"
                            small
                        >
                          <div>
                            <div class="font-weight-normal">
                              <v-row>
                                <strong>{{ item.title }}</strong>
                              </v-row>
                              @{{ item.time }}
                            </div>
                            <div>{{ item.log }}</div>
                            <v-img
                                :src="item.pic"
                                contain
                                max-height="200"
                            >
                            </v-img>
                          </div>
                        </v-timeline-item>
                      </v-timeline>
                    </v-card-text>

                    <v-card-actions>
                      <v-spacer/>
                      <v-btn color="blue darken-1" text @click="closefixlog">
                        关 闭
                      </v-btn>
                    </v-card-actions>
                  </v-card>
                </v-dialog>
                </template>
              </v-data-table>

            </template>

          </v-card>
        </v-col>

        <v-col cols="12" lg="6" xl="5">
          <v-card class="pa-10 fill-height elevation-5" outlined>
            <div class="d-flex align-center justify-space-between">
              <div class="text-h6">
                公 告
              </div>
            </div>
            <v-hover
                v-slot="{ hover }"
                close-delay="200"
            >
            <v-carousel
                :cycle="hover? false : true"
                hide-delimiter-background
                show-arrows-on-hover
                v-if="poster.length!==0"
            >
              <v-carousel-item v-for="item in poster" :key="item.poster_id">
                <v-card
                    class="mx-auto"
                    color="#26c6da"
                    height="100%">
                  <v-card-title>
                    <v-icon large left>mdi-twitter</v-icon>
                    <span class="title font-weight-light">{{item.poster_title}}</span>
                  </v-card-title>
                  <v-card-text >{{item.poster_log}}</v-card-text>
                  <v-card-actions>
                    <v-list-item class="grow">
                      <v-list-item-avatar color="grey darken-3">
                        <v-img
                            class="elevation-6"
                            alt=""
                            src="data:image/svg+xml;base64,"
                        ></v-img>
                      </v-list-item-avatar>
                      <v-list-item-content>
                        <v-list-item-title>{{item.admin_name}}</v-list-item-title>
                      </v-list-item-content>
                      <v-list-item-content>
                        <v-list-item-title>发布时间：{{item.time}}</v-list-item-title>
                      </v-list-item-content>
                    </v-list-item>
                  </v-card-actions>
                </v-card>
              </v-carousel-item>
            </v-carousel>

            <v-carousel
                hide-delimiters
                hide-delimiter-background
                show-arrows-on-hover
                v-else>
              <v-carousel-item>
                <v-sheet
                    color="#26c6da"
                    height="100%"
                    tile
                >
                  <v-row
                      class="fill-height"
                      align="center"
                      justify="center"
                  >
                    <div class="display-3">
                      暂无公告
                    </div>
                  </v-row>
                </v-sheet>
              </v-carousel-item>
            </v-carousel>
            </v-hover>
          </v-card>
        </v-col>
      </v-row>
    </v-sheet>
    <v-snackbar
        top
        v-model="bar1"
        :timeout="3000"
    >
      {{ mess }}
      <template v-slot:action="{ attrs }">
        <v-btn
            color="blue"
            text
            v-bind="attrs"
            @click="bar1 = false"
        >
          关 闭
        </v-btn>
      </template>
    </v-snackbar>
  </div>

</template>

<script>
import { validationMixin } from 'vuelidate'
import {maxLength, required,} from 'vuelidate/lib/validators'
import {mdiTimelineTextOutline} from '@mdi/js'
export default {
  mixins: [validationMixin],
  validations: {
    savelog:{required,maxLength:maxLength(50)},
  },
  data: () => ({
    dialogfixlog:false,
    mdiTimelineTextOutline:mdiTimelineTextOutline,
    memo_sort:'电',
    memo:['电','水','燃气','网络'],
    url: process.env.VUE_APP_API,
    load:true,
    bar1:false,dialog:false,
    headers: [
      {text: '姓 名', align: 'start', value: 'name'},
      { text: '金 额', value: 'charge_cost'},
      { text: '类 型', value: 'charge_memo' },
      { text: '截止日期', value: 'charge_ddl'},
      { text: 'Actions', value: 'actions', sortable: false },
    ],
    fixheader: [
      { text: '详 情',align: 'start', value: 'fix_log',sortable: false},
      { text: '报修时间', value: 'fix_startime'},
      { text: '维修时间', value: 'fix_endtime'},
      { text: '维修状态', value: 'fix_status'},
      { text: 'Actions', value: 'actions', sortable: false },
    ],
    chart: false,
    desserts: [],
    fix:[],
    mess:"",
    poster:[],
    login:window.sessionStorage.getItem('loginname'),
    name:window.sessionStorage.getItem('name'),
    savelog:'',
    tl:[],
  }),
  mounted() {
    this.chart = true;
  },
  created () {
    this.initialize()
  },
  computed: {
    logErrors () {
      const errors = []
      if (!this.$v.savelog.$dirty) return errors
      !this.$v.savelog.required && errors.push('详情不可为空')
      !this.$v.savelog.maxLength && errors.push('详情不可超过50个字符')
      return errors
    },
  },
  methods: {
    timelineItem(item){
      this.dialogfixlog = true
      var mess = {'id':item.fix_id}
      this.axios.post(this.url+'userFix/queryFixlog',JSON.stringify(mess))
          .then(res => {
            this.tl=res.data;
            this.loadin=!this.loadin;
            this.dialogfixlog = true
          },res => {
            console.log(res);
          })
    },
    closefixlog(){
      this.dialogfixlog = false
      this.tl=null
    },
    getColor (calories) {
      if (calories === "已处理") return 'primary'
      else return 'red'
    },

    initialize() {
      this.load = true
      var mess = {"login":this.login}
      this.axios.get(this.url+'user/poster')
          .then(res => {
            this.poster=res.data
          }, res => {
            console.log(res);
          })
      this.axios.post(this.url+'cust/money',JSON.stringify(mess))
          .then(res => {
            this.desserts = res.data
          }, res => {
            console.log(res);
          })
      this.axios.post(this.url+'cust/fix',JSON.stringify(mess))
          .then(res => {
            this.fix = res.data
          }, res => {
            console.log(res);
          })
      this.load = false
    },
    close () {
      this.dialog = false
      this.savelog=""
      this.$v.savelog.$reset()
    },

    paymoney(item){
      this.axios.post(this.url+'cust/paymoney', JSON.stringify(item))
          .then(res => {
            this.mess = res.data
            this.bar1 = true
            this.initialize()
          },res => {
            console.log(res);
          })
    },
    save () {
      if(this.$v.savelog.$invalid||this.$v.savelog.$error){
        this.$v.savelog.$touch()
      }
      else{
        var mess = {"log":this.savelog,"login":this.name,"fix_sort":this.memo_sort,
          "time":new Date().toJSON().substring(0, 10) + ' ' + new Date().toTimeString().substring(0,8)}
        this.axios.post(this.url+'cust/AddFix', JSON.stringify(mess))
        this.mess = "报修成功"
        this.bar1 = true
        this.initialize()
        this.close()
        this.$v.savelog.$reset()
      }
    },
  },
};
</script>
