<template>
  <div class="px-4">
    <v-sheet color="transparent">
      <v-row>
        <v-col cols="12" md="6" lg="4" v-for="(item, i) in stats" :key="i">
          <v-card class="py-6 px-10 elevation-5" outlined>
            <div>
              <div class="d-flex justify-space-between align-center">
                <div>
                  <div class="subtitle-2 text--secondary">
                    {{ item.label }}
                  </div>
                  <div class="text-h6 mt-1">
                    {{ item.title }}
                  </div>
                </div>

                <div>
                  <v-icon :color="item.color"  x-large v-if="i===0">{{ item.icon }}</v-icon>
                  <v-progress-circular
                      v-else
                      :rotate="270"
                      :size="50"
                      :width="10"
                      :value="item.number"
                      :color="item.color"
                  >
                  </v-progress-circular>
                </div>
              </div>
              <div class="subtitle-2 d-flex align-center mt-5">
                <div>
                  <span class="success--text">{{ item.number }}</span>
                  <span class="ml-2 text--secondary">
                    {{ item.desc }}
                  </span>
                </div>
              </div>
            </div>
          </v-card>
        </v-col>
      </v-row>

      <v-row>
        <v-col cols="12" lg="6" xl="7">
          <v-card class="pa-10 elevation-5" outlined>
            <div class="d-flex align-center justify-space-between">
              <div>
                <div class="text-h6">
                  待处理缴费信息
                </div>
              </div>
            </div>
            <template>
              <v-data-table
                  :headers="headers"
                  :items="desserts"
                  :items-per-page="5"
                  multi-sort
                  class="elevation-24"
                  :loading = "load"
                  loading-text="Loading... Please wait"
              >
                <template v-slot:item.actions="{ item }">
                  <v-btn color="error" class="elevation-5" @click="moneyalert(item)">提 醒</v-btn>
                </template>
                <template v-slot:no-data>
                  暂无待处理缴费信息
                </template>
              </v-data-table>
            </template>
          </v-card>
          <v-col></v-col>

          <v-card outlined class="pa-10 elevation-5">
            <div class="d-flex align-center justify-space-between">
              <div class="text-h6">
                待处理维修记录
              </div>

            </div>

            <template>
              <v-data-table
                  :headers="fixheader"
                  :items="fix"
                  :items-per-page="5"
                  multi-sort
                  class="elevation-24"
              >
                <template v-slot:no-data>
                  暂无待处理维修记录
                </template>
              </v-data-table>

            </template>

          </v-card>
        </v-col>

        <v-col cols="12" lg="6" xl="5">
          <v-card class="pa-10 fill-height elevation-5" outlined>
            <div class="d-flex align-center justify-space-between">
              <div class="text-h6">
                公 告
              </div>
            </div>
            <v-hover
                v-slot="{ hover }"
                close-delay="200"
            >
            <v-carousel
                :cycle="hover? false : true"
                height="400"
                hide-delimiter-background
                show-arrows-on-hover
                v-if="poster.length!==0"
            >
              <v-carousel-item v-for="item in poster" :key="item.poster_id" >
                        <v-card
                            class="mx-auto"
                            color="#26c6da"
                            height="100%">
                          <v-card-title>
                            <v-icon large left> {{twitter}}</v-icon>
                            <span class="title font-weight-light">{{item.poster_title}}</span>
                          </v-card-title>
                            <v-card-text >{{item.poster_log}}</v-card-text>
                          <v-card-actions>
                            <v-list-item class="grow">
                              <v-list-item-avatar color="grey darken-3">
                                <v-img
                                    class="elevation-6"
                                    alt=""
                                    src="../../static/head.svg"
                                ></v-img>
                              </v-list-item-avatar>
                              <v-list-item-content>
                                <v-list-item-title>{{item.admin_name}}</v-list-item-title>
                              </v-list-item-content>
                              <v-list-item-content>
                                <v-list-item-title>发布时间：{{item.time}}</v-list-item-title>
                              </v-list-item-content>
                            </v-list-item>
                          </v-card-actions>
                        </v-card>
              </v-carousel-item>
            </v-carousel>

            <v-carousel
                height="400"
                hide-delimiters
                hide-delimiter-background
                show-arrows-on-hover
                v-else>
              <v-carousel-item>
                <v-sheet
                    color="#26c6da"
                    height="100%"
                    tile
                >
                  <v-row
                      class="fill-height"
                      align="center"
                      justify="center"
                  >
                    <div class="display-3">
                   暂无公告
                    </div>
                  </v-row>
                </v-sheet>
              </v-carousel-item>
            </v-carousel>
            </v-hover>
            <v-col v-if="id==='1'">
              <div class="text-h6">
                未发布公告
              </div>
              <template>
                <v-data-table
                    :headers="posterheader"
                    :items="unposter"
                    :items-per-page="5"
                    class="elevation-24"
                    multi-sort
                    loading-text="无未发布的公告"
                    loading=false
                >
                  <template v-slot:item.actions="{ item }">
                    <v-btn color="primary" class="elevation-5" @click="post(item)" >立即发布</v-btn>
                  </template>
                </v-data-table>
              </template>
            </v-col>

            <v-col v-else>
              <div class="text-h6">
                待签发公告
              </div>
              <template>
                <v-data-table
                    :headers="posterheader"
                    :items="unsign"
                    :items-per-page="5"
                    class="elevation-24"
                    multi-sort
                >
                  <template v-slot:no-data>暂无待签发的公告</template>
                  <template v-slot:item.actions="{ item }">
                    <v-btn color="primary" class="elevation-5" @click="dialogg(item)" >签 字</v-btn>
                  </template>
                </v-data-table>
              </template>
            </v-col>
          </v-card>
        </v-col>
      </v-row>
    </v-sheet>
    <v-dialog
        v-model="dialog"
        persistent
        max-width="400"
    >
      <v-card
          class="mx-auto"
          max-width="400"
      >
        <v-img
            class="white--text align-end"
            height="200px"
            :src="iconBase64"
        >
          <v-card-title>
            <span class="headline">上传签字文件</span>
          </v-card-title>
        </v-img>

        <v-card-text class="text--primary">
            <v-col>
              <v-file-input
                  counter
                  chips
                  truncate-length="15"
                  accept=".jpg"
                  @change="see"
                  v-model="fileInfo"
                  :error-messages="fileErrors"
                  @input="$v.fileInfo.$touch()"
                  @blur="$v.fileInfo.$touch()"
              ></v-file-input>
            </v-col>
          <v-col>
            <v-col
                class="subtitle-1 text-center"
                cols="12"
                v-if="viewquary === null"
            >
              识别中
            </v-col>
              <v-progress-linear

                  v-if="viewquary === null"
                  color="primary accent-4"
                  indeterminate
                  rounded
                  height="6"
              ></v-progress-linear>
            <v-text-field
                v-if="viewquary !== null"
              outlined
              readonly
              label="识别结果"
              v-model="viewquary"
          ></v-text-field>
          </v-col>
          <small>*indicates required field</small>
        </v-card-text>
        <v-card-actions>
          <v-spacer/>
          <v-btn
              color="blue darken-1"
              text
              @click="close"
          >
            关 闭
          </v-btn>
          <v-btn
              color="blue darken-1"
              text
              @click="postsign"
          >
            Save
          </v-btn>
        </v-card-actions>
      </v-card>
    </v-dialog>
    <v-snackbar
        top
        v-model="bar1"
        :timeout="3000"
    >
      {{ mess }}
      <template v-slot:action="{ attrs }">
        <v-btn
            color="blue"
            text
            v-bind="attrs"
            @click="bar1 = false"
        >
          Close
        </v-btn>
      </template>
    </v-snackbar>
  </div>

</template>

<script>
import { validationMixin } from 'vuelidate'
import { required,} from 'vuelidate/lib/validators'
import { mdiAccountGroup,mdiTwitter } from '@mdi/js'
export default {
  data: () => ({
    load:true,
    viewquary:'',
    iconBase64:null,
    base:"data:image/jpeg;base64,",
    fileInfo:null,
    twitter:mdiTwitter,
    bar1:false,
    headers: [
      {text: '姓 名', align: 'start', value: 'name'},
      { text: '金 额', value: 'charge_cost'},
      { text: '类 型', value: 'charge_memo' },
      { text: '截止日期', value: 'charge_ddl'},
      { text: 'Actions', value: 'actions', sortable: false },
    ],
    fixheader: [
      {text: '姓 名', align: 'start', value: 'name'},
      { text: '地 址', value: 'addr',sortable: false},
      { text: '详 情', value: 'fix_log',sortable: false},
    ],
    posterheader: [
      {text: '标 题', align: 'start', value: 'poster_title',width:100, sortable: false,},
      { text: '发布时间', value: 'time'},
      { text: '截止时间', value: 'endtime',},
      { text: 'Actions', value: 'actions', sortable: false, },
    ],
    chart: false,
    stats: [
      {label: "当前业主数", title: "", number: "", desc: "",
        icon: mdiAccountGroup, color: "error",
      },
      {label: "待处理缴费条目", title: "", number: "", desc: "",
        color: "warning",
      },
      {label: "待处理维修条目", title: "", number: "", desc: "",
        color: "accent",
      },
    ],
    id:window.sessionStorage.getItem("identity"),
    desserts: [],
    fix:[],
    mess:"",
    window: 0,
    poster:[],
    unposter:[],
    unsign:[],
    dialog:false,
    editedIndex: -1,
    editedItem:{},
    defaultItem: {},
  }),
  mixins: [validationMixin],
  validations: {
    fileInfo:{required},
  },
  computed:{
    fileErrors() {
      const errors = []
      if (!this.$v.fileInfo.$dirty) return errors
      !this.$v.fileInfo.required && errors.push('文件不可为空')
      return errors
    },
  },
  mounted() {this.chart = true;},
  created () {this.initialize()},
  methods: {
    close () {
      this.dialog = false
      this.$nextTick(() => {
        this.editedItem = Object.assign({}, this.defaultItem)
        this.editedIndex = -1
      })
      this.$v.$reset()
      this.iconBase64 = this.base
      this.fileInfo = null
      this.viewquary=''

    },
    see(file) {
      if (!(this.$v.$invalid||this.$v.$error)){
        this.viewquary = null
        var reader = new FileReader()
        reader.readAsDataURL(file)
        reader.onload = () => {
          this.iconBase64 = reader.result
          var mess = {"base":this.iconBase64}
          this.axios.post('/api/user/view',JSON.stringify(mess))
          .then(res=>{
              this.viewquary = res.data
          })
        }
        reader.onerror = function (error) {
          console.log('Error: ', error)
        }
      }
    },
    initialize() {
      this.iconBase64 = this.base
      this.axios.get('/api/user/poster')
          .then(res => {
            this.poster=res.data
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/unposter')
          .then(res => {
            this.unposter=res.data
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/unsign')
          .then(res => {
            this.unsign=res.data
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/custnum')
          .then(res => {
            this.stats[0]['title']=res.data
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/todaymoney')
          .then(res => {
            this.stats[1]['title']=res.data['fail']
            this.stats[1]['number']=res.data['succ']
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/fix')
          .then(res => {
            this.stats[2]['title']=res.data['fail']
            this.stats[2]['number']=res.data['succ']
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/money')
          .then(res => {
            this.desserts = res.data
          }, res => {
            console.log(res);
          })
      this.axios.get('/api/user/todayfix')
          .then(res => {
            this.fix = res.data
          }, res => {
            console.log(res);
          })
      this.load = false
    },
    moneyalert(item){
      this.axios.post('/api/user/moneyalert', JSON.stringify(item))
          .then(res => {
            this.mess = res.data["mess"]
            this.bar1 = true
            this.initialize()
          },res => {
            console.log(res);
          })
    },
    post(item){
      var mess = {"id":item.poster_id}
      this.axios.post('/api/user/post', JSON.stringify(mess))
          .then(res => {
            this.mess = res.data["mess"]
            this.bar1 = true
            this.initialize()
          },res => {
            console.log(res);
          })
    },
    dialogg (item) {
      this.editedIndex = this.unsign.indexOf(item)
      this.editedItem = Object.assign({}, item)
      this.dialog = true
    },
    postsign(){
      var mess = {"id":this.editedItem.poster_id}
      this.axios.post('/api/user/postsign', JSON.stringify(mess))
          .then(res => {
            this.mess = res.data["mess"]
            this.bar1 = true
            this.close()
            this.initialize()
          },res => {
            console.log(res);
          })

    },
  },
};
</script>
